            if (!dbContext.PartTypes.Any())
            {
                var partTypes = new List<PartType>
                {
                    new PartType { Id = "video1.1", PartId = "CD1.1", MediaPath = "https://www.youtube.com/watch?v=5NcXWhsyojg", Content = "1.\tThực dân Pháp phát hiện và tổ chức khai thác, quản lí vùng đất Đà Lạt –Lâm Đồng\r\nThực dân Pháp nhận thấy vùng đất ngày nay là Tây Nguyên tuy hoang sơ nhưng giàu tiềm năng nên đã tiến hành những cuộc khảo sát rồi khai thác trên quy mô lớn. Đầu năm 1893, bác sĩ Alexandre Yersin (1863 – 1943) được Toàn quyền Đông Dương giao nhiệm vụ khảo sát một tuyến đường bộ từ Sài Gòn lên Cao nguyên Trung phần và kết thúc ở bờ biển Trung Kì.\r\nTháng 6/1893, Yersin tiến hành thám sát vùng núi Lang Biang – Lâm Viên. Ngày 21/6/1893, ông bước ra khỏi rừng thông và nhìn thấy một cao nguyên tuyệt đẹp: cao nguyên Lâm Viên. Đây chính là khoảnh khắc “phát hiện ra Đà Lạt”.\r\nNgày 1/11/1899, Toàn quyền Đông Dương đã quyết định thành lập tỉnh Đồng Nai Thượng với hai trạm hành chính: một ở Tánh Linh và một ở Lang Biang, tỉnh lị đặt tại Djiring. Đây là tiền đề pháp lí cho việc hình thành đô thị Đà Lạt sau này nói riêng và quá trình quản lí, khai thác vùng đất Lâm Đồng.\r\nNhững năm tiếp theo, người Pháp đã tiến hành quy hoạch xây dựng và phát triểnĐà Lạt một cách bài bản, khoa học.\r\nTừ ngày 8/1/1941, tỉnh Lâm Viên được lập lại với tỉnh lị đặt tại Đà Lạt. Tỉnh lị tỉnh Đồng Nai Thượng chuyển về Djiring.", ViewStatus = "1" },
                    new PartType { Id = "image1.2", PartId = "CD1.1", MediaPath = "https://drive.google.com/file/d/1Mf-mrLN8gltvDNMRA_YiwkxiuEMsiJfD/view?usp=sharing", Content = "2.\tTình hình kinh tế – xã hội hai tỉnh Lâm Viên, Đồng Nai Thượng dưới sự cai trị của thực dân Pháp \r\nThực dân Pháp đẩy mạnh khai thác tài nguyên và mở đồn điền. Ở các vùng đất màu mỡ và dọc các trục đường giao thông quan trọng, chính quyền Pháp đã chiếm đoạt hàng ngàn héc ta đất canh tác của đồng bào các dân tộc thiểu số để lập đồn điền.\r\nGắn liền với quá trình xây dựng Đà Lạt và khai thác kinh tế, thực dân Pháp đã chiêu mộ người Kinh từ các tỉnh khác đến Lâm Viên, Đồng Nai Thượng. Đó là những người dân lao động cần cù, chất phác vì bị áp bức bóc lột không thể nào sống được ở quê nhà\r\nLà người dân của một nước thuộc địa, nhân dân hai tỉnh Lâm Viên, Đồng Nai Thượng bị thực dân Pháp bóc lột tàn tệ, phải làm những công việc nặng nhọc, nguy hiểm\r\n", ViewStatus = "1" },
                    new PartType { Id = "audio2.1", PartId = "CD1.2", MediaPath = "https://drive.google.com/file/d/1EXmevpsphWoOHRorlq2SqB44GtWdpsML/view?usp=sharing", Content = "1.\tQuá trình ra đời chi bộ cộng sản đầu tiên ở Lâm Viên\r\nNăm 1928, Tân Việt Cách mạng đảng, một tổ chức yêu nước đã có cơ sở ở Trung Kì, Nam Kì và các tỉnh Nam Trung Kì. Đầu năm 1929, một Chi bộ Tân Việt được thành lập tại Đà Lạt. Chi bộ Tân Việt Đà Lạt trực thuộc Liên tỉnh Ngũ Trang – phụ trách các tỉnh Khánh Hoà, Ninh Thuận, Bình Thuận, Đắk Lắk, Lâm Viên và Đồng Nai Thượng.\r\nTháng 4/1930, Liên tỉnh Ngũ Trang tổ chức Hội nghị ở Tân Mỹ (Ninh Thuận) để công bố việc hợp nhất các tổ chức cộng sản, thành lập các chi bộ Đảng Cộng sản Việt Nam ở các tỉnh cực Nam. Sau khi dự Hội nghị ở Tân Mỹ, ông Trần Diệm triệu tập hội nghị trên tầng gác căn buồng số 2 nhà xe Khách sạn Palace (Đà Lạt) để bàn việc giải thể Chi bộ Tân Việt và thành lập chi bộ cộng sản. Hội nghị thành công tốt đẹp với việc thành lập chi bộ Đảng Cộng sản Việt Nam đầu tiên của tỉnh Lâm Viên. Chi bộ gồm ba đảng viên do ông Trần Diệm làm Bí thư.\r\nSự ra đời của chi bộ cộng sản đầu tiên đánh dấu bước ngoặt lịch sử của phong trào cách mạng ở Lâm Viên – Đồng Nai Thượng.\r\n", ViewStatus = "1" },
                    new PartType { Id = "video2.2", PartId = "CD1.2", MediaPath = "https://www.youtube.com/watch?v=8dGW8_TbD4o", Content = "2.\tPhong trào cách mạng 1930 – 1931 ở Lâm Viên – Đồng Nai Thượng\r\nNhững năm 1929 – 1933, thế giới lâm vào cuộc khủng hoảng kinh tế rất nghiêm trọng. Từ các nước tư bản, cuộc khủng hoảng lan nhanh sang các thuộc địa, trong đó có Việt Nam. Ở Lâm Viên và Đồng Nai Thượng, các ngành kinh tế đều chịu ảnh hưởng nặng nề.\r\nỞ Lâm Viên và Đồng Nai Thượng, các ngành kinh tế đều chịu ảnh hưởng nặng nề.\r\nHưởng ứng phong trào cách mạng 1930 – 1931 dâng cao trên cả nước, công nhân xây dựng hãng Đờ-ra-ga (Dragage) ở Đà Lạt đã bãi công đòi chủ không được sa thải công nhân và không được giảm lương. Cuộc bãi công tuy không giành được thắng lợi nhưng đã đánh dấu sự chuyển biến về ý thức, trình độ giác ngộ và có ảnh hưởng lớn về chính trị, tư tưởng trong giai cấp công nhân và nhân dân Lâm Viên, Đồng Nai Thượng.\r\nNhân dịp kỉ niệm Ngày Quốc tế Lao động 1/5/1930, chi bộ Đảng và các tổ chứcquần chúng đã tổ chức nhiều hoạt động để cổ vũ tinh thần đấu tranh của quần chúngnhân dân. Tối 30/4/1930 và trong ngày 1/5/1930, cờ đỏ búa liềm xuất hiện nhiều nơi.\r\nNgày 4/5/1930, công nhân trên công trường đường hầm Cầu Đất thuộc tuyến đường sắt Tháp Chàm – Đà Lạt đã tổ chức bãi công đòi chủ trả các tháng lương còn thiếu, cải thiện điều kiện lao động và điều kiện an toàn lao động. Trước tinh thần đoàn kết và đấu tranh quyết liệt của công nhân, tên chủ hãng nhận trả cho công nhân một nửa số lương mà tên cai thầu còn thiếu. Cuộc bãi công đã giành được thắng lợi\r\nĐầu năm 1931, các chi bộ và các tổ chức quần chúng chuẩn bị truyền đơn kỉ niệm ngày Quốc tế Phụ nữ 8/3. Công việc đang chuẩn bị khẩn trương thì bị thực dân Pháp khủng bố. Ngày 6/3/1931, một số đảng viên và cơ sở cách mạng bị bắt trong khi đang in truyền đơn và tài liệu của Đảng, ba đảng viên còn lại tạm lánh vào Sài Gòn nhưng đến ngày 24/4/1931 cũng bị địch bắt. Từ đây, các chi bộ Đảng và tổ chức quầnchúng ở Lâm Đồng không còn nữa, phong trào cách mạng tạm thời lắng xuống. Như vậy, đến cuối tháng 4/1931, tổ chức Đảng không còn, các tổ chức quầnchúng ngừng hoạt động, phong trào công nhân và phong trào cách mạng ở Lâm Viên và Đồng Nai Thượng tạm lắng xuống. Trong những năm 1932 – 1935, chỉ có một số cuộc đấu tranh lẻ tẻ, không thu hút được đông đảo quần chúng tham gia.\r\n", ViewStatus = "1" },
                    new PartType { Id = "video3.1", PartId = "CD1.3", MediaPath = "https://www.youtube.com/watch?v=dwWnENoZU5s", Content = "1.\tHoàn cảnh lịch sử\r\nSau cuộc khủng hoảng kinh tế thế giới 1929 – 1933, chủ nghĩa phát xít ra đời ở nhiều nước như Đức, Ý, Nhật Bản,… Điều này đe doạ nghiêm trọng đến hoà bình và an ninh thế giới.\r\nTrong hoàn cảnh đó, Quốc tế Cộng sản đã triệu tập Đại hội lần thứ VII tại Matxcơva (tháng 7/1935) và quyết định thành lập ở mỗi nước Mặt trận nhân dân nhằm tập hợpcác lực lượng tiến bộ chống chủ nghĩa phát xít, chống chiến tranh và bảo vệ hoà bình.\r\nTình hình thế giới đã tác động đến cuộc đấu tranh cách mạng ở Việt Nam. Đảng quyết định tạm gác khẩu hiệu “độc lập dân tộc, người cày có ruộng”, thay bằng khẩu hiệu chống phát xít, chống bọn phản động tay sai, đòi tự do, dân chủ, cơm áo và hoà bình. Đảng thành lập Mặt trận Nhân dân phản đế Đông Dương (từ năm 1938 đổi thành Mặt trận Dân chủ Đông Dương) để tập hợp nhân dân đấu tranh. Dưới sự lãnh đạo của Đảng và Mặt trận Dân chủ Đông Dương, phong trào đấu tranh đòi tự do, dân chủ đã diễn ra suốt các năm 1936 – 1939 trên cả nước.\r\nỞ Lâm Viên và Đồng Nai Thượng, trong các năm 1936 – 1937, tổ chức Đảng vẫn chưa được khôi phục. Tuy nhiên, tin tức về phong trào đấu tranh của các địa phương trong cả nước và qua báo chí công khai của Đảng, phong trào đấu tranh đòi các quyền dân tộc, dân chủ đã ảnh hưởng đến nhận thức và khơi dậy tinh thần đấu tranh của giai cấp công nhân và nhân dân các dân tộc. Công nhân ở từng vùng đã đoàn kết cùng nhau đấu tranh để hưởng ứng phong trào chung.\r\n", ViewStatus = "1" },
                    new PartType { Id = "image3.2", PartId = "CD1.3", MediaPath = "https://drive.google.com/file/d/1LtXYUg1drRjY0rRRSWoJr49cx6USMMfa/view?usp=drive_link", Content = "2.\tPhong trào cách mạng ở Lâm Viên – Đồng Nai Thượng những năm 1936–1939 \r\nNăm 1939, Chiến tranh thế giới thứ hai do các nước phát xít Đức, Ý, Nhật châm ngòi đã chính thức nổ ra. Tháng 6/1940, nước Pháp bị phát xít Đức chiếm đóng; nhâncơ hội đó phát xít Nhật nhảy vào xâm lược Đông Dương. Nhân dân ta lại bị một cổ hai tròng dưới sự áp bức của Pháp – Nhật.\r\n\r\nLo sợ trước tình hình chiến tranh ngày càng mở rộng, thực dân Pháp có ý đồ xâydựng Đà Lạt thành thủ đô chính trị của Đông Dương. Số người Pháp lên Đà Lạt ngàycàng đông, nhiều công trình lớn và hàng trăm biệt thự được xây dựng.\r\n\r\nNgày 9/3/1945, phát xít Nhật đảo chính, lật đổ chính quyền Pháp ở Đông Dương.Tại Lâm Viên, Đồng Nai Thượng, Nhật bắt hàng trăm quan chức, kiều dân Phápgiam tại cư xá Đờ-cu (Decoux), cư xá Bên-lơ-vuy (Bellevue) ở Đà Lạt và khách sạnMin-lo-voay (Minllevoye) ở Djiring. \r\n\r\nVới ý đồ chuyển Bộ Chỉ huy quân đội Nhật ở Thái Bình Dương đến Đà Lạt để cốthủ đến cùng nên ngoài 2 000 lính Nhật tại chỗ, chúng còn ráo riết bắt lính, xây dựng các công trình quân sự, bảo vệ chặt chẽ sân bay Liên Khàng (nay là Liên Khương).\r\nCùng với phong trào công nhân và hoạt động của các tổ chức quần chúng, từ giữa năm 1937 đã nổ ra cuộc nổi dậy của đồng bào các dân tộc thiểu số dưới sự lãnh đạo của hai thủ lĩnh K’Voai và K’Nhòi – một phụ nữ trẻ tuổi, có nước da trắng, tóc trắng được nhân dân gọi là\r\nMộ Cọ (bà trắng).\r\nPhong trào bắt đầu từ Đồng Đò (Djiring) sau đó phát triển đến hầu hết các huyện trong tỉnh, thu hút gần 10 000 người tham gia.Phong trào kéo dài đến tháng 5/1938 thì bị dập tắt. Hai thủ lĩnh bị bắt và mỗi người bịkết án 20 năm tù khổ sai.\r\nTừ khi Chiến tranh thế giới thứ hai bùng nổ (tháng 9/1939), thực dân Pháp tăng cường khủng bố phong trào cách mạng nước ta. Tại Đà Lạt, một số cán bộ, đảng viên và thành viên chủ chốt trong nhóm Tiến bộ bị bắt. Từ cuối tháng 10/1939, chi bộ Đảng ở Đà Lạt không còn, các tổ chức quần chúng cũng ngừng hoạt động.\r\nSau 4 năm hưởng ứng phong trào Mặt trận Dân chủ Đông Dương, giai cấp công nhân và nhân dân lao động Lâm Viên, Đồng Nai Thượng đã tiến hành 17 cuộc đấu tranh, thu hút hàng vạn lượt người tham gia; thành lập được 5 tổ chức quần chúngvới trên 500 hội viên.\r\n", ViewStatus = "1" },
                    new PartType { Id = "video4.1", PartId = "CD1.4", MediaPath = "https://www.youtube.com/watch?v=ILbPXBv15GQ", Content = "1.\tTình hình Lâm Viên – Đồng Nai Thượng trong Chiến tranh thế giới thứ hai (1939 – 1945) và phong trào cách mạng trước năm 1945 (type: video)\r\nNăm 1939, Chiến tranh thế giới thứ hai do các nước phát xít Đức, Ý, Nhật châm ngòi đã chính thức nổ ra. Tháng 6/1940, nước Pháp bị phát xít Đức chiếm đóng; nhân cơ hội đó phát xít Nhật nhảy vào xâm lược Đông Dương. Nhân dân ta lại bị một cổ hai tròng dưới sự áp bức của Pháp – Nhật.\r\n\r\nLo sợ trước tình hình chiến tranh ngày càng mở rộng, thực dân Pháp có ý đồ xây dựng Đà Lạt thành thủ đô chính trị của Đông Dương. Số người Pháp lên Đà Lạt ngày càng đông, nhiều công trình lớn và hàng trăm biệt thự được xây dựng.\r\nNgày 9/3/1945, phát xít Nhật đảo chính, lật đổ chính quyền Pháp ở Đông Dương.Tại Lâm Viên, Đồng Nai Thượng, Nhật bắt hàng trăm quan chức, kiều dân Pháp giam tại cư xá Đờ-cu (Decoux), cư xá Bên-lơ-vuy (Bellevue) ở Đà Lạt và khách sạn Min-lo-voay (Minllevoye) ở Djiring.\r\nVới ý đồ chuyển Bộ Chỉ huy quân đội Nhật ở Thái Bình Dương đến Đà Lạt để cố thủ đến cùng nên ngoài 2 000 lính Nhật tại chỗ, chúng còn ráo riết bắt lính, xây dựng các công trình quân sự, bảo vệ chặt chẽ sân bay Liên Khàng (nay là Liên Khương).\r\nDưới sự thống trị của Pháp – Nhật, đời sống nhân dân Lâm Viên – Đồng Nai Thượng ngày càng cơ cực. Các tầng lớp nhân dân chỉ chờ dịp để nổi dậy chống lại kẻ thù.\r\n", ViewStatus = "1" },
                    new PartType { Id = "image4.2", PartId = "CD1.4", MediaPath = "https://drive.google.com/file/d/1D-dTaBZ3ld4gRZIxHJm_kR9vYaIL8BwD/view?usp=drive_link", Content = "2.\tCách mạng tháng Tám năm 1945 trên địa bàn Lâm Viên – Đồng Nai Thượng \r\nĐầu năm 1945, tình hình thế giới có nhiều chuyển biến quan trọng. Ở châu Âu, phát xít Đức liên tiếp bại trận. Ở châu Á – Thái Bình Dương, phát xít Nhật cũng dần bị đẩy lui trên các mặt trận.\r\nTrước những đổi thay nhanh chóng của tình hình thế giới, cao trào kháng Nhậtcứu nước ngày càng lên cao, Mặt trận Việt Minh phát triển khắp nơi đã có ảnh hưởng đến đời sống chính trị ở hai tỉnh Lâm Viên, Đồng Nai Thượng. Ở một số đồn điền, côngnhân nổi dậy đấu tranh. Đồng bào các dân tộc thiểu số ở một số nơi không chịu nộpthuế, đi xâu cho phát xít Nhật.\r\nTháng 4/1945, tù chính trị ở nhà lao Buôn Ma Thuột đấu tranh thắng lợi, buộc Nhật phải thả hết tù chính trị. Tổ chức Đảng ở nhà lao phân công đảng viên về các tỉnh hoạt động, xây dựng cơ sở cách mạng. Tháng 5/1945, Tỉnh bộ Việt Minh lâm thời tỉnhLâm Viên do ông Ngô Huy Diễn làm Thư kí và Uỷ ban Mặt trận Việt Minh Đà Lạt do ông Nguyễn Thế Tính làm Thư kí được thành lập.\r\nNgày 13/8/1945, khi nhận được tin về việc Nhật Bản sắp đầu hàng, Trung ương Đảng và Tổng bộ Việt Minh thành lập Uỷ ban Khởi nghĩa toàn quốc. Ngày 14 và ngày15/8/1945, Hội nghị toàn quốc của Đảng họp ở Tân Trào (Tuyên Quang) quyết địnhlãnh đạo toàn dân tổng khởi nghĩa.\r\nĐược sự phối hợp của Uỷ ban Khởi nghĩa tỉnh Khánh Hoà, đêm 21/8/1945, hội nghị bàn việc khởi nghĩa giành chính quyền tỉnh Lâm Viên được tiến hành tại Đà Lạt.Hội nghị thông qua chủ trương, phương pháp vận động, tổ chức nhân dân tiến hành khởi nghĩa, thành lập Uỷ ban khởi nghĩa và quyết định khởi nghĩa giành chính quyềnvào ngày 23/8/1945.\r\nNgay trong ngày 22/8/1945, một số cán bộ, đảng viên ở Cầu Đất, Trạm Hành,Dran đã phát động nhân dân đứng lên khởi nghĩa thắng lợi. Đêm 22/8/1945, lực lượng tự vệ, thanh niên ở đây kéo xuống Djiring buộc Tỉnh trưởng Đồng Nai Thượng phải đầu hàng. Tuy nhiên, chính quyền cách mạng chưa được thành lập ở Đồng Nai Thượng.\r\nTại Đà Lạt, sáng sớm ngày 23/8/1945, từ các địa bàn ngoại ô, các tầng lớp nhândân hàng ngũ chỉnh tề mang theo cờ đỏ sao vàng, khẩu hiệu, các loại vũ khí thô sơrầm rộ kéo về tập trung trước nhà Thông tin triển lãm (nay là khu vực trước rạp 3/4) dựmít tinh. Đoàn biểu tình của đồng bào các dân tộc thiểu số ở tổng Lạch (xã Lát, huyện Lạc Dương) cũng kéo về Đà Lạt tham gia giành chính quyền.\r\nSau khi dự mít tinh, đoàn biểu tình kéo đến bao vây Dinh Tỉnh trưởng Lâm Viên(nay là nhà số 4 đường Thủ Khoa Huân), buộc viên tỉnh trưởng phải đầu hàng. Sauđó, đoàn biểu tình kéo đến chiếm đồn bảo an, phá cửa nhà lao đón những đồng chí,đồng bào đang bị giam.\r\nTiếp đó, đoàn biểu tình kéo đến bao vây dinh Tổng đốc Lâm – Đồng – Bình –Ninh. Viên tổng đốc không chịu đầu hàng nên bị bắt giam. Sau đó, Uỷ ban Khởi nghĩa cử các đoàn cán bộ đến tiếp quản các công sở.\r\nTối 23/8, Uỷ ban Khởi nghĩa và những cán bộ tham gia khởi nghĩa họp đánh giá tình hình và thành lập Uỷ ban nhân dân Cách mạng lâm thời tỉnh Lâm Viên gồm 7 thành viên do ông Phan Đức Huy làm Chủ tịch.\r\nSau khi tỉnh Lâm Viên khởi nghĩa giành thắng lợi, chính quyền cách mạng từ tỉnh đến cơ sở được thành lập, Uỷ ban nhân dân Cách mạng lâm thời tỉnh nhận thấy ởtỉnh Đồng Nai Thượng vẫn chưa thành lập chính quyền cách mạng nên đã cử đại diệnxuống giúp tỉnh thành lập chính quyền.\r\nChiều 28/8/1945, Uỷ ban nhân dân Cách mạng lâm thời tỉnh Đồng Nai Thượng được thành lập, gồm 6 thành viên do ông Nguyễn Đại Hoà làm Chủ tịch. Hôm sau, Uỷ ban Việt Minh tỉnh được thành lập. Những ngày sau đó các đoàn thể quần chúng và chính quyền cách mạng cũng lần lượt ra đời.\r\nNhư vậy, trong vòng 7 ngày (từ 22 đến 28/8/1945), nhân dân hai tỉnh Lâm Viên và Đồng Nai Thượng đã tiến hành khởi nghĩa giành chính quyền thắng lợi, thành lập chính quyền cách mạng và Mặt trận Việt Minh từ tỉnh đến cơ sở.\r\nCách mạng tháng Tám năm 1945 ở Lâm Viên và Đồng Nai Thượng diễn ra tronglúc tổ chức Đảng ở địa phương còn yếu, số lượng đảng viên ít, có người bị bắt, bịtù đày nhưng vẫn nêu cao ý chí cách mạng và vai trò lãnh đạo, phát huy được sứcmạnh tổng hợp của các tầng lớp nhân dân, đưa cuộc khởi nghĩa giành thắng lợi nhanhchóng, ít bị tổn thất.\r\nCách mạng tháng Tám năm 1945 ở Lâm Viên và Đồng Nai Thượng thắng lợi đãgóp phần cùng cả nước đập tan ách thống trị của thực dân Pháp, phát xít Nhật và lật đổ chế độ phong kiến tồn tại dài lâu trên đất nước ta, đưa dân tộc Việt Nam bước vào kỉ nguyên mới, kỉ nguyên độc lập, tự do và nhân dân làm chủ đất nước.\r\n", ViewStatus = "0" },
                    
